import osfp = open('PathLabel.txt', 'w')path = 'F:\AudioEmotion\\'file_Names = os.listdir(path)for fileName in file_Names:    audioNames = os.listdir(path + fileName)    for audioName in audioNames:        audioNameSplit = audioName.split('-')        EmotionLabel = int(audioNameSplit[2])        fp.write(path + fileName + '\\'+ audioName +' '+ str(EmotionLabel))        fp.write('\n')fp.close()